import React from 'react';
import { StyleSheet, ScrollView, Text } from 'react-native';
import { DataTable } from 'react-native-paper';

const TableExample = () => {
return (
	<ScrollView horizontal>
	<DataTable style={styles.container}>
	<DataTable.Header style={styles.tableHeader}>
		<DataTable.Title>Packet No.</DataTable.Title>
		<DataTable.Title>Type</DataTable.Title>
		<DataTable.Title>Shape</DataTable.Title>
		<DataTable.Title>Pcs</DataTable.Title>
		<DataTable.Title>Clarity</DataTable.Title>
		<DataTable.Title>Color</DataTable.Title>
		<DataTable.Title>Carat</DataTable.Title>
		<DataTable.Title>Size Range</DataTable.Title>
	</DataTable.Header>
	<DataTable.Row>
		<DataTable.Cell style={styles.tableRightBorder}>LG-75988</DataTable.Cell>
		<DataTable.Cell style={styles.tableRightBorder}>Single</DataTable.Cell>
		<DataTable.Cell style={styles.tableRightBorder}>EMERALD</DataTable.Cell>
		<DataTable.Cell style={styles.tableRightBorder}>1</DataTable.Cell>
		<DataTable.Cell style={styles.tableRightBorder}>VVS2</DataTable.Cell>
		<DataTable.Cell style={styles.tableRightBorder}>E</DataTable.Cell>
		<DataTable.Cell style={styles.tableRightBorder}>0.69</DataTable.Cell>
		<DataTable.Cell style={styles.tableRightBorder}>0.5.00-O.69</DataTable.Cell>
	</DataTable.Row>

	<DataTable.Row>
		<DataTable.Cell></DataTable.Cell>
		<DataTable.Cell></DataTable.Cell>
		<DataTable.Cell></DataTable.Cell>
	</DataTable.Row>
	<DataTable.Row>
		<DataTable.Cell></DataTable.Cell>
		<DataTable.Cell></DataTable.Cell>
		<DataTable.Cell></DataTable.Cell>
	</DataTable.Row>
	<DataTable.Row>
		<DataTable.Cell></DataTable.Cell>
		<DataTable.Cell></DataTable.Cell>
		<DataTable.Cell></DataTable.Cell>
	</DataTable.Row>
	</DataTable>
	<DataTable.Pagination
				page={1}
				numberOfPages={3}
				onPageChange={(page) => { console.log(page); }}
				label="1-2 of 6"
		/>
	</ScrollView>
);
};

export default TableExample;

const styles = StyleSheet.create({
container: {
	padding: 1,
	// border: '1px solid blue',
},
tableHeader: {
	backgroundColor: '#DCDCDC',
	// border: '1px solid red',
	width: '100%',
},
tableRightBorder: {
	borderWidth: 0,
	borderRightWidth: 1,
	borderColor: 'black',
	borderStyle: 'solid',
}
});
