import { StyleSheet, Text, View, SafeAreaView, Image } from "react-native";
import React, { useState } from "react";
import { KeyboardAvoidingView } from "react-native";
import { MaterialIcons } from "@expo/vector-icons";
import { AntDesign } from "@expo/vector-icons";
import { TextInput } from "react-native";

const LoginScreen = () => {

  const [email, setEmail] = useState("");
  const [Password, setPassword] = useState("");

  return (
    <SafeAreaView
      style={{ flex: 1, backgroundColor: "#fff", alignItems: "center" }}
    >
      <View>
        <Image
          style={{ width: 150, height: 100 }}
          source={{
            uri: "https://assets.stickpng.com/thumbs/6160562276000b00045a7d97.png",
          }}
        />
      </View>

      <KeyboardAvoidingView>
        <View style={{ alignItems: "center" }}>
          <Text
            style={{
              fontSize: 17,
              fontWeight: "bold",
              marginTop: 12,
              color: "#041e42",
            }}
          >
            Login in to your Account
          </Text>
        </View>

        <View style={{ marginTop: 70 }}>
        <View
          style={{
            flexDirection: "row",
            alignItems: "center",
            gap: 5,
            backgroundColor: "#d0d0d0",
            paddingVertical: 5,
            borderRadius: 5,
            marginTop: 30,
          }}
        >
          <MaterialIcons style={{ marginLeft: 8 }} name="email" size={24} color="grey" />
          <TextInput value={email} onChangeText={(text) => setEmail(text)} style={{ color: 'gray', marginVertical: 10, width: 300, fontSize:email ? 16 : 16 }} placeholder="Enter Your Email" />
        </View>
      </View>

      <View style={{ marginTop: 30 }}>
        <View
          style={{
            flexDirection: "row",
            alignItems: "center",
            gap: 5,
            backgroundColor: "#d0d0d0",
            paddingVertical: 5,
            borderRadius: 5,
            marginTop: 10,
          }}
        >
          <AntDesign
            name="lock1"
            size={24}
            color="gray"
            style={{ marginLeft: 8 }}
          />
          <TextInput value={Password} onChangeText={(text => setPassword(text))} secureTextEntry={true} style={{ color: 'gray', marginVertical: 10, width: 300, fontSize:Password ? 16 : 16 }} placeholder="Enter Your Password" />
        </View>
      </View>

      <View>
        <Text style={{marginTop:12, flexDirection:'row', alignItems:'center', justifyContent:'space-between' }}>Keep me Logged In</Text>

        <Text>Forget Passowrd?</Text>
      </View>

      </KeyboardAvoidingView>
    </SafeAreaView>
  );
};

export default LoginScreen;

const styles = StyleSheet.create({});
